Workflow.Definition:
"@@ConnectionString: ''
@@Type: 'Local'
@@DesktopType: 'local'
@@DisplayName: 'Local computer'
IMPORT 'controlRepo.appmask' AS appmask
IMPORT 'imageRepo.imgrepo' AS imgrepo
@SENSITIVE: []
@INPUT FileName : { 'Description': '''Dynamic filename from Power Apps
''', 'FriendlyName': 'FileName', 'Type': 'String', 'IsOptional': False, 'DefaultValue': '' } 
@INPUT Base64Audio : { 'Description': '''Cleaned base64 audio from Power Apps
''', 'FriendlyName': 'Base64Audio', 'Type': 'String', 'IsOptional': False, 'DefaultValue': '' } 
@INPUT FolderName : { 'Description': 'Local folder path for audio and transcript files', 'FriendlyName': 'FolderName', 'Type': 'String', 'IsOptional': False, 'DefaultValue': '' } 
@OUTPUT TranscriptionText : { 'Description': '''Final transcription text returned to Cloud Flow
''', 'FriendlyName': 'TranscriptionText', 'Type': 'String' } 
Scripting.RunPowershellScript.RunScript Script: $'''# ============================================
# PowerShell 5.1 – Whisper bridge (simple labels)
#
# From PAD you send:
# %FileName% - file name without extension
# %FolderName% - folder where files are saved
# %Base64Audio% - audio in base64
#
# Script does:
# 1. Make a .wav file from base64
# 2. Send .wav to Whisper on port 9000
# 3. Save transcript as .txt
# 4. Print transcript back to PAD
# ============================================

$ErrorActionPreference = \'Stop\'

# ---------- BASIC SETTINGS ----------
# Change this folder or URL if needed.
$DefaultFolder = \'C:\\WhisperData\'
$WhisperUrl = \'http://127.0.0.1:9000/asr?encode=true&task=transcribe&language=en&output=srt\'

# ---------- HELPER: STOP WITH ERROR ----------
function Stop-WithError {
 param([string]$Message)

 Write-Output \"ERROR: $Message\"
 exit 1
}

# ---------- HELPER: SEND WAV FILE TO WHISPER ----------
function Invoke-WhisperMultipart {
 param(
 [string]$FilePath,
 [string]$Url
 )

 if (-not (Test-Path -LiteralPath $FilePath)) {
 Stop-WithError \"WAV file not found: $FilePath\"
 }

 # Build multipart/form-data body --GPTBUilD---
 $boundary = \'------------------------\' + ([System.Guid]::NewGuid().ToString(\'N\'))
 $LF = \"`r`n\"
 $fileNameOnly = [System.IO.Path]::GetFileName($FilePath)

 $header =
 \"--$boundary$LF\" +
 \"Content-Disposition: form-data; name=`\"audio_file`\"; filename=`\"$fileNameOnly`\"$LF\" +
 \"Content-Type: audio/wav$LF$LF\"

 $footer = \"$LF--$boundary--$LF\"

 $headerBytes = [System.Text.Encoding]::ASCII.GetBytes($header)
 $fileBytes = [System.IO.File]::ReadAllBytes($FilePath)
 $footerBytes = [System.Text.Encoding]::ASCII.GetBytes($footer)

 $bodyBytes = New-Object byte[] ($headerBytes.Length + $fileBytes.Length + $footerBytes.Length)
 [Array]::Copy($headerBytes, 0, $bodyBytes, 0, $headerBytes.Length)
 [Array]::Copy($fileBytes, 0, $bodyBytes, $headerBytes.Length, $fileBytes.Length)
 [Array]::Copy($footerBytes, 0, $bodyBytes, $headerBytes.Length + $fileBytes.Length, $footerBytes.Length)

 $contentType = \"multipart/form-data; boundary=$boundary\"

 try {
 $response = Invoke-RestMethod -Uri $Url -Method Post -ContentType $contentType -Body $bodyBytes
 }
 catch {
 Stop-WithError (\"Whisper HTTP call failed: \" + $_.Exception.Message)
 }

 return $response
}

# ============================================
# MAIN SCRIPT
# ============================================

# ---------- STEP 1: GET VALUES FROM PAD ----------
$FileName = \'%FileName%\'
$FolderName = \'C:\\WhisperData\'
$Base64Audio = \'%Base64Audio%\'

# ---------- STEP 2: CHECK BASIC INPUT ----------
if ([string]::IsNullOrWhiteSpace($Base64Audio)) {
 Stop-WithError \'No Base64Audio value was sent from PAD.\'
}

if ([string]::IsNullOrWhiteSpace($FileName)) {
 $FileName = \'audio_\' + (Get-Date -Format \'yyyyMMdd_HHmmss\')
}

if ([string]::IsNullOrWhiteSpace($FolderName)) {
 $FolderName = $DefaultFolder
}

# Make sure folder exists
if (-not (Test-Path -LiteralPath $FolderName)) {
 try {
 New-Item -ItemType Directory -Path $FolderName -Force | Out-Null
 }
 catch {
 Stop-WithError (\"Could not create folder: \" + $_.Exception.Message)
 }
}

$wavPath = Join-Path $FolderName ($FileName + \'.wav\')
$txtPath = Join-Path $FolderName ($FileName + \'.txt\')

# ---------- STEP 3: BASE64 → WAV ----------
# Remove spaces/newlines before decode
$cleanBase64 = $Base64Audio -replace \'\\s\',\'\'

try {
 $wavBytes = [System.Convert]::FromBase64String($cleanBase64)
}
catch {
 Stop-WithError (\"Base64 decode failed: \" + $_.Exception.Message)
}

try {
 [System.IO.File]::WriteAllBytes($wavPath, $wavBytes)
}
catch {
 Stop-WithError (\"Could not write WAV file: \" + $_.Exception.Message)
}

# ---------- STEP 4: CALL WHISPER ----------
$response = Invoke-WhisperMultipart -FilePath $wavPath -Url $WhisperUrl

# ---------- STEP 5: GET TEXT FROM RESPONSE ----------
$transcript = $null

if ($response -is [string]) {
 # Text or SRT
 $transcript = $response
}
elseif ($response -ne $null -and $response.PSObject.Properties.Name -contains \'text\') {
 # JSON: { \"text\": \"...\" }
 $transcript = $response.text
}
else {
 # Unknown shape – show raw JSON
 $transcript = ($response | ConvertTo-Json -Compress)
}

if ([string]::IsNullOrWhiteSpace($transcript)) {
 $transcript = \'ERROR: Whisper returned empty text.\'
}

# ---------- STEP 6: SAVE TRANSCRIPT ----------
try {
 [System.IO.File]::WriteAllText($txtPath, $transcript, [System.Text.Encoding]::UTF8)
}
catch {
 $transcript += \"`n[Warning: Could not write transcript file: $($_.Exception.Message)]\"
}

# ---------- STEP 7: SEND TEXT BACK TO PAD ----------
Write-Output $transcript
''' ScriptOutput=> TranscriptFromWhisper
SET TranscriptionText TO TranscriptFromWhisper
"
